insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (1, N'Phương tiện tham gia giao thông đường bộ" gồm những loại nào?', N'Phương tiện giao thông cơ giới đường bộ.', N'Phương tiện giao thông thô sơ đường bộ.', N'Xe máy chuyên dùng.', N'Cả ba loại trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (2, N'Người tham gia giao thông đường bộ" gồm những thành phần nào?', N'Người điều khiển, người sử dụng phương tiện tham gia giao thông đường bộ.', N'Người điều khiển, dẫn dắt súc vật.', N'Người đi bộ trên đường bộ.', N'Cả ba thành phần nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (3, N'Khái niệm "đường bộ" được hiểu như thế nào là đúng ?', N'Đường bộ" gồm: Đường, cầu đường bộ, hầm đường bộ.', N'Đường bộ" gồm: Đường, cầu đường bộ, hầm đường bộ, bến phà đường bộ.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (4, N'Khái niệm "Phần đường xe chạy" được hiểu như thế nào là đúng?', N'Là phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại.', N'Là phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại, dải đất dọc hai bên đường để đảm bảo an toàn giao thông.', N'Cả hai ý trên.', null, 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (5, N'Khái niệm "Công trình đường bộ" được hiểu như thế nào là đúng ?', N'Công trình đường bộ" gồm: Đường bộ, nơi dừng xe, đỗ xe trên đường, đèn tím hiệu, cọc tiêu, biển báo hiệu, dải phân cách.', N'Công trình đường bộ" gồm: Đường bộ, nơi dừng xe, đỗ xe trên đường, đèn tín hiệu, cọc tiêu, biển báo hiệu, dải phân cách, hệ thống thoát nước và công trình, thiết bị phụ trợ khác.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (6, N'Khái niệm "Làn đường" được hiểu như thế nào là đúng?', N'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường.', N'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn.', null , null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (7, N'Khái niệm "Khổ giới hạn của đường bộ" được hiểu như thế nào là đúng?', N'Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, hầm trên đường bộ để các xe kể cả hàng hoá xếp trên xe đi qua được an toàn.', N'Là khoảng trống có kích thước giới hạn về chiều rộng của đường, cầu, hầm trên đường bộ để các xe kể cả hàng hoá xếp trên xe đi qua được an toàn.', N'Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, hầm trên đường bộ để các xe đi qua được an toàn.', null, 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (8, N'Khái niệm "Dải phân cách"được hiểu như thế nào là đúng?', N'Là bộ phận của đường để phân chia mặt đường thành hai chiều chạy riêng biệt.', N'Là bộ phận của đường để phân chia phần đường của xe cơ giới và xe thô sơ.', N'Cả hai ý trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (9, N'Có mấy loại dải phân cách?', N'Loại cố định.', N'Loại di động.', N'Cả hai loại trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (10, N'Khi điều khiển xe qua cầu, qua phà cần chú ý những điểm gì?', N'Chấp hành nghiêm chỉnh các biển báo hiệu, tín hiệu nếu có.', N'Phải tuyệt đối tuân thủ theo sự điều khiển chỉ dẫn của người gác cầu hoặc nhân viên bến phà.', N'Cả 2 ý nêu trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (11, N'Phương tiện giao thông đường bộ" gồm những loại nào?', N'Phương tiện giao thông cơ giới đường bộ.', N'Phương tiện giao thông thô sơ đường bộ.', N'Cả hai loại nêu trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (12, N'Phương tiện giao thông cơ giới đường bộ" gồm những loại nào?', N'Ô-tô, máy kéo, xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy.', N'Ô-tô, máy kéo, xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy và các loại xe tương tự, kể cả xe cơ giới dùng cho người tàn tật.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (13, N'Phương tiện giao thông thô sơ đường bộ" gồm những loại nào?', N'Những loại xe không di chuyển bằng sức động cơ như xe đạp, xe xích lô.', N'Xe súc vật kéo và các loại xe tương tự.', N'Cả hai ý trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (14, N'Người điều khiển phương tiện tham gia giao thông" gồm những thành phần nào? ', N'Người điều khiển xe cơ giới.', N'Người điều khiển xe thô sơ.', N'Người điều khiển xe máy chuyên dùng tham gia giao thông đường bộ.', N'Cả ba thành phần nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (15, N'Người điều khiển giao thông" gồm những thành phần nào?', N'Người điều khiển phương tiện tham gia giao thông.', N'Cảnh sát giao thông, người được giao nhiệm vụ hướng dẫn giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (16, N'Người tham gia giao thông phải làm gì để đảm bảo an toàn giao thông đường bộ? ', N'Phải nghiêm chỉnh chấp hành quy tắc giao thông.', N'Phải giữ gìn an toàn cho mình và cho người khác.', N'Cả hai ý trên.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (17, N'Bảo đảm an toàn giao thông đường bộ là trách nhiệm của ai?', N'Là trách nhiệm của ngành Giao thông vận tải.', N'Là trách nhiệm của các cơ quan, tổ chức, cá nhân và của toàn xã hội.', N'Là trách nhiệm của Cảnh sát giao thông.', null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (18, N'Mọi hành vi vi phạm Luật giao thông đường bộ được xử lý như thế nào?', N'Phải được xử lý nghiêm minh.', N'Phải được xử lý kịp thời.', N'Phải được xử lý đúng pháp luật.', N'Cả ba ý trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (19, N'Người lái xe đang điều khiển xe trên đường mà trong máu có nồng độ cồn vượt quá bao nhiêu thì bị nghiêm cấm?', N'Nồng độ cồn vượt quá 60 miligam/100 mililít máu.', N'Nồng độ cồn vượt quá 80 miligam/100 mililít máu.', N'Nồng độ cồn vượt quá 100 miligam/100 mililít máu.', null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (20, N'Người lái xe đang điều khiển xe trên đường mà trong khí thở có nồng độ cồn vượt quá bao nhiêu thì bị nghiêm cấm?', N'Nồng độ cồn vượt quá 40miligam/1 lít khí thở.', N'Nồng độ cồn vượt quá 60miligam/1 lít khí thở.', N'Nồng độ cồn vượt quá 80miligam/1 lít khí thở.', null, 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (21, N'Người tham gia giao thông phải đi như thế nào là đúng quy tắc giao thông?', N'Đi bên phải theo chiều đi của mình.', N'Đi đúng phần đường quy định.', N'Chấp hành hệ thống báo hiệu đường bộ.', N'Tất cả các ý trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (22, N'Biển báo hiệu đường bộ gồm những nhóm nào, ý nghĩa của từng nhóm?', N'Nhóm biển báo cấm để biểu thị các điều cấm, nhóm biển báo nguy hiểm để cảnh báo các tình huống nguy hiểm có thể xảy ra.', N'Nhóm hiệu lệnh để báo các hiệu lệnh phải thi hành, Nhóm biển chỉ dẫn để chỉ dẫn hướng đi hoặc các điều cần biết.', N'Nhóm biển phụ để thuyết minh bổ sung các loại biển báo cấm, biển báo nguy hiểm, biển hiệu lệnh và biển chỉ dẫn.', N'Tất cả các nhóm nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (23, N'Người lái xe phải làm gì khi điều kiển xe vào đường cao tốc?', N'Phải có tín hiệu xin vào và phải nhường đường cho xe đang chạy trên đường.', N'Khi thấy an toàn mới cho xe nhập vào dòng xe ở làn đường sát mép ngoài.', N'Nếu có làn đường tăng tốc thì phải cho xe chạy trên làn đường đó trước khi vào các làn đường của đường cao tốc.', N'Tất cả các ý nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (24, N'Khi điều khiển xe qua cầu hẹp cần chú ý những điểm gì?', N'Dùng số thấp giữ đều ga cho xe qua từ từ, không được đi sát rìa cầu, không tăng ga đột ngột, đổi số hoặc phanh gấp trên cầu.', N'Dùng số phù hợp, cho xe qua từ từ, không được đi sát rìa cầu, không tăng ga đột ngột, đổi số hoặc phanh gấp trên cầu.', null, null, 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (25, N'Khi điều khiển xe trên đường trơn cần chú ý những điểm gì?', N'Giữ vững tay lái cho xe đi đúng vệt bánh xe đi trước, sử dụng số thấp đi chậm, giữ đều ga, đánh lái ngoặt và phanh gấp khi cần thiết.', N'Giữ vững tay lái cho xe đi đúng vệt bánh xe đi trước, sử dụng số thấp đi chậm, giữ đều ga, không lấy nhiều lái, không đánh lái ngoặt và phanh gấp.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (26, N'Người điều khiển phưong tiện tham gia giao thông trong hầm đường bộ phải tuân thủ những điểm gì là đúng quy tắc giao thông?', N'Xe cơ giới phải bật đèn ngay cả khi đường hầm sáng, xe thô sơ phải có đèn hoặc có vật phát sáng báo hiệu.', N'Chỉ được dừng xe, đỗ xe ở những nơi quy định.', N'Không được quay đầu xe, lùi xe.', N'Tất cả các ý trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (27, N'Trường hợp xe kéo xe và kéo rơmoóc, những hành vi nào bị cấm?', N'Xe kéo rờ moóc, xe sơ mi rơ moóc kéo theo rơ moóc hoặc xe khác.', N'Chở người trên xe được kéo.', N'Xe ôtô kéo theo xe thô sơ, xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy hoặc kéo lê vật trên đường.', N'Tất cả các ý nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (28, N'Người tham gia giao thông khi phát hiện công trình đường bộ bị hư hỏng hoặc bị xâm hại, phải có những nghĩa vụ gì?', N'Kịp thời báo cáo cho chính quyền địa phương.', N'Kịp thời báo cáo cho cơ quan quản lý đường bộ hoặc cơ quan công an nơi gần nhất, để xử lý.', N'Trong trường hợp cần thiết có biện pháp báo hiệu ngay cho người tham gia giao thông biết.', N'Tất cả các nghĩa vụ trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (29, N'Khi điều khiển xe giảm số cần chú ý những điểm gì?', N'Không được nhìn xuống buồng lái, cần phải giảm theo thứ tự từ cao đến thấp, phối hợp các động tác phải nhịp nhàng, chính xác.', N'Không được nhìn xuống buồng lái, cần phải giảm theo thứ tự từ cao đến thấp, phối hợp các động tác phải nhịp nhàng, chính xác, vù ga phải phù hợp với tốc độ.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (30, N'Chủ xe và người lái xe bánh xích, xe quá khổ, quá tải có trách nhiệm gì khi cho xe tham gia giao thông đường bộ?', N'Xin phép lưu hành đặc biệt.', N'Chịu sự kiểm soát về tải trọng và khổ giới hạn của cơ quan quản lý đường bộ.', N'Chịu phí tổn gia cố, bảo vệ công trình giao thông, hạ tải, xếp lại hàng hoá, tự bảo quản hàng đã bị dỡ xuống và nộp phạt theo quy định.', N'Tất cả các trách nhiệm trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (31, N'Những người có mặt tại nơi xảy ra tai nạn giao thông có trách nhiệm gì?', N'Bảo vệ hiện trường, giúp đỡ, cứu chữa kịp thời, bảo vệ tài sản của người bị nạn.', N'Báo tin ngay cho cơ quan công an hoặc Uỷ ban nhân dân nơi gần nhất.', N'Cung cấp thông tin sát thực về vụ tai nạn theo yêu cầu của cơ quan công an.', N'Tất cả ba trách nhiệm nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (32, N'Trên đường giao thông, khi hiệu lệnh của người điều khiển giao thông trái với hiệu lệnh của đèn hoặc biển báo thì người tham gia giao thông phải chấp hành theo hiệu lệnh nào?', N'Hiệu lệnh của người điều khiển giao thông.', N'Hiệu lệnh của đèn điều khiển giao thông.', N'Hiệu lệnh của biển báo hiệu đường bộ.', null, 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (33, N'Khi ở một khu vực đồng thời có đặt biển báo cố định và biển báo tạm thời mà ý nghĩa hiệu lực khác nhau, thì người lái xe phải chấp hành theo hiệu lệnh nào?', N'Biển báo cố định.', N'Biển báo tạm thời.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (34, N'Cơ quan nào quy định tải trọng, khổ giới hạn của đường bộ?', N'Bộ Giao thông vận tải.', N'Cục Đường bộ Việt Nam.', N'Sở Giao thông vận tải, Giao thông công chính các tỉnh thành phố.', N'Cảnh sát giao thông.', 1);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (35, N'Trong trường hợp đặc biệt, xe quá tải trọng, quá khổ giới hạn của đường bộ khi lưu hành phải xin giấy phép của cơ quan nào?', N'Cơ quan Cảnh sát giao thông có thẩm quyền.', N'Cơ quan Quản lý giao thông có thẩm quyền.', N'Uỷ ban nhân dân tỉnh.', null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (36, N'Hai xe đi ngược chiều nhường đường khi tránh nhau như thế nào là đúng quy tắc giao thông?', N'Nơi đường hẹp chỉ đủ cho một xe chạy và có chỗ tránh xe thì xe nào ở gần chỗ tránh hơn phải vào vị trí tránh,nhường đường cho xe kia đi.', N'Xe xuống dốc phải nhường đường cho xe lên dốc.', N'Xe nào có chướng ngại vật phía trước phải nhường đường cho xe kia đi.', N'Tất cả các ý nêu trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (37, N'Xe ôtô tham gia giao thông trên đường phố có bắt buộc phải có bộ phận giảm thanh, giảm khói không?', N'Không bắt buộc.', N'Bắt buộc.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (38, N'Xe ôtô tham gia giao thông trên đường phải có các loại đèn gì?', N'Đèn chiếu sáng gần và xa.', N'Đèn soi biển số.', N'Đèn báo hãm và đèn tín hiệu.', N'Có đủ các loại đèn ghi trên.', 4);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (39, N'Ban đêm, xe cơ giới đi ngược chiều gặp nhau, đèn chiếu sáng phải sử dụng như thế nào là đúng quy tắc giao thông?', N'Chuyển từ đèn chiếu gần sang đèn chiếu xa.', N'Phải chuyển từ đèn chiếu xa sang đèn chiếu gần.', null, null, 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (40, N'Xe cơ giới 2-3 bánh có được kéo đẩy nhau hoặc vật gì khác trên đường không? ', N'Được phép.', N'Tuỳ trường hợp.', N'Tuyệt đối không.', null, 3);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (41, N'Trong đô thị, đoạn đường có dải phân cách cố định, khi không có biển hạn chế tốc độ, với điều kiện đường khô ráo và thời tiết bình thường, loại phương tiện nào chạy với tốc độ tối đa 50km/h?', N'Xe xích lô máy, xe gắn máy.', N'Các loại xe con, xe taxi đến 9 chỗ ngồi.', N'Xe tải có tải trọng từ 3.500 kg trở lên, xe ôtô chở người trên 30 chỗ ngồi.', N'Xe môtô 2-3 bánh, xe tải có tải trọng dưới 3.500kg, xe ôtô chở người từ 10 đến 30 chỗ ngồi.', 2);
insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (42, N'Trong đô thị, đoạn đường có dải phân cách cố định, khi không có biển hạn chế tốc độ, với điều kiện đường khô ráo và thời tiết bình thường, loại phương tiện nào chạy với tốc độ tối đa 40km/h?', N'Xe xích lô máy, xe gắn máy.', N'Các loại xe con, xe taxi đến 9 chỗ ngồi.', N'Xe tải có tải trọng từ 3.500 kg trở lên, xe ôtô chở người trên 30 chỗ ngồi.', N'Xe môtô 2-3 bánh, xe tải có tải trọng dưới 3.500kg, xe ôtô chở người từ 10 đến 30 chỗ ngồi.', 4);
