insert into Question(SttCau, CauHoi, CauA, CauB, CauC, CauD, DapAn) values (118, N'Biển nào báo hiệu sắp đến chỗ giao nhau với đường sắt?', N'Biển 1.', N'Biển 1 và 2.', N'Biển 2 và 3.', N'Cả 3 biển.', 4);
