create database DoAnWindows
go
use DoAnWindows
go
create table Question
(
	SttCau	int					primary key,
	CauHoi	nvarchar(500),
	CauA	nvarchar(500),
	CauB	nvarchar(500),
	CauC	nvarchar(500),
	CauD	nvarchar(500),
	DapAn	int
);
go
create table ThongTin
(
	Username	varchar(20)		primary key,
	Pass		varchar(50),
	HoTen		nvarchar(50),
	NgaySinh	nvarchar(20),
	DiaChi		nvarchar(50),
	SDT			nvarchar(20),
);
go
create table ThongKe
(
	username	varchar(20)		foreign key references ThongTin(Username),
	ThoiGian	varchar(30),
	DiemSo		int
);

